def send_hourly_congratulations():
    today_birthdays = get_today_birthdays()
    if not today_birthdays:
        logger.info("–°–µ–≥–æ–¥–Ω—è –Ω–µ—Ç –∏–º–µ–Ω–∏–Ω–Ω–∏–∫–æ–≤.")
        return

